import React, { Component } from "react";

class Addbook extends Component{
    constructor(){
       super();
       this.state={
           field: {},        
           Bookid: "",
           BookName:"",
           Author:"",
           Edition:"",
           Date:""
       }
       this.handleChange=this.handleChange.bind(this);
       this.SavaData=this.bind(this);
    };
    handleChange = event => {
        let field=this.state.field;
        field[event.target.name]=event.target.value;
        this.setState({
            field
        });
    };
    SavaData =event =>{
        event.preventDefault();
        
    }
    render(){
        return (
        <form id="form" onSubmit={this.SavaData}>
Bookid: <input name="id" type="number" value={this.state.field.Bookid} onchange={this.handleChange} />
<br />
BookName: <input name="Book-name" type="text" value={this.state.field.BookName} onchange={this.handleChange} />
<br />
Author: <input name="Name" type="text" value={this.state.field.Author} onchange={this.handleChange}/>
<br />
Edition: <input name="Name" type="text" value={this.state.field.Edition} onchange={this.handleChange}/>
<br />
Date: <input name="date" type="date" value={this.state.field.Date} onchange={this.handleChange} />
<br />
<input type="submit" name="submit"  />
</form>
);

    }
}
export default Addbook;
