import React, { Component } from "react";
import './style.css';
import { Box } from "@material-ui/core";
class Addbook extends Component{
    user;
    // field=[];
    constructor(props){
       super(props);
       this.state={ 
        field :[{   
           Bookid: " ",
           BookName:" ",
           Author:" ",
           Edition:" ",
           Date:" ",}]
       };
      
       this.handleChange=this.handleChange.bind(this);
       this.SavaData=this.SavaData.bind(this);
    }
    
    handleChange = event => {
       
        this.setState({[event.target.name]:event.target.value});
   
    }
    SavaData(event){
        // event.preventDefault()
        // localStorage.setItem('field',JSON.stringify(this.state)); 
        var myObjectJson = JSON.stringify(this.state);
        localStorage.setItem("field",myObjectJson);
        var newMyObjectJSON = localStorage.getItem("field");
        var newMyObject = JSON.parse(newMyObjectJSON);  
    }
     
    componentDidMount(){
        try {
            this.user=JSON.parse(localStorage.getItem('field'))
            this.setState ( {
                Bookid:this.user.Bookid,
                BookName:this.user.BookName,
                Author:this.user.Author,
                Edition:this.user.Edition,
                Date:this.user.Date

            })
        } catch {

        }

    }
    onSubmit (e){
        e.preventDefault()
        this.setState({
            Bookid: " ",
           BookName:" ",
           Author:" ",
           Edition:" ",
           Date:" ",
           
        })
    }
    add() {
        var title = this.title.value;
        if(localStorage.getItem('field')==null) {
            var field =[];
            field.push(title);
            localStorage.setItem('field',JSON.stringify(field));
        }else{
            var field= JSON.parse(localStorage.getItem('field'));
            field.push(title);
            localStorage.setItem('field',JSON.stringify(field));
        }
        this.setState({
            field: JSON.parse(localStorage.getItem('field'))
        });
    }
    render(){
        return (
        <form className="Display_form"  float='left' onSubmit={this.SavaData}>
            <Box float ='Left'>
Bookid: <input name="Bookid" type="number" value={this.state.Bookid} ref='title' onChange={this.handleChange} />
<br />
BookName: <input name="BookName" type="text" value={this.state.BookName} ref='title' onChange={this.handleChange} />
<br />
Author: <input name="Author" type="text" value={this.state.Author} ref='title' onChange={this.handleChange}/>
<br />
Edition: <input name="Edition" type="text" value={this.state.Edition} ref='title' onChange={this.handleChange}/>
<br />
Date: <input name="Date" type="date" value={this.state.Date} ref='title' onChange={this.handleChange} />
<br />
<input type="submit" name="submit"  onClick={this.add.bind(this)} />
</Box>
</form>
);
    }
}
export default Addbook;
