import React, { Component } from "react";
import './style.css';
class Addbook extends Component{
    user;
    constructor(props){
       super(props);
       this.state={
                   
           Bookid: " ",
           BookName:" ",
           Author:" ",
           Edition:" ",
           Date:" ",
       }
       this.handleChange=this.handleChange.bind(this);
       this.SavaData=this.SavaData.bind(this);
    }
    
    handleChange = event => {
       
        this.setState({[event.target.name]:event.target.value});
   
    }
    SavaData(event){
        event.preventDefault()
        localStorage.setItem('field',JSON.stringify(this.state));
        
    }
    componentDidMount(){
        try {
            this.user=JSON.parse(localStorage.getItem('field'))
            this.setState({
                Bookid:this.user.Bookid,
                BookName:this.user.BookName,
                Author:this.user.Author,
                Edition:this.user.Edition,
                Date:this.user.Date

            });
        } catch {

        }

    }
    onSubmit (e){
        e.preventDefault()
        this.setState({
            Bookid: " ",
           BookName:" ",
           Author:" ",
           Edition:" ",
           Date:" ",
           
        })
    }
    render(){
        return (
        <form className="Display_form" onSubmit={this.SavaData}>
Bookid: <input name="Bookid" type="number" value={this.state.Bookid} onChange={this.handleChange} />
<br />
BookName: <input name="BookName" type="text" value={this.state.BookName} onChange={this.handleChange} />
<br />
Author: <input name="Author" type="text" value={this.state.Author} onChange={this.handleChange}/>
<br />
Edition: <input name="Edition" type="text" value={this.state.Edition} onChange={this.handleChange}/>
<br />
Date: <input name="Date" type="date" value={this.state.Date} onChange={this.handleChange} />
<br />
<input type="submit" name="submit"  />
</form>
);
    }
}
export default Addbook;
