import React, { Component } from "react";

class Addbook extends Component{
    user;
    constructor(props){
       super(props);
       this.state={
                   
           Bookid: "",
           BookName:"",
           Author:"",
           Edition:"",
           Date:"",
       }
       this.handleChange=this.handleChange.bind(this);
       this.SavaData=this.SavaData.bind(this);
    }
    
    handleChange = event => {
        // let field=this.state.field;
        this.setState({[event.target.name]:event.target.value});
    //  this.setState({
    //         field
    //     });
    // }
    }
    SavaData(event){
        event.preventDefault()
        localStorage.setItem('document',JSON.stringify(this.state));
        
    }
    componentDidMount(){
        this.user=JSON.parse(localStorage.getItem('field'))
        this.setState({
            Bookid:this.user.Bookid,
            BookName:this.user.BookName,
            Author:this.user.Author,
            Edition:this.user.Edition,
            Date:this.user.Date

        });
    }
   
    
    
   
    render(){
        return (
        <form id="form" onSubmit={this.SavaData}>
Bookid: <input name="Bookid" type="number" value={this.state.field.Bookid} onChange={this.handleChange} />
<br />
BookName: <input name="BookName" type="text" value={this.state.field.BookName} onChange={this.handleChange} />
<br />
Author: <input name="Author" type="text" value={this.state.field.Author} onChange={this.handleChange}/>
<br />
Edition: <input name="Edition" type="text" value={this.state.field.Edition} onChange={this.handleChange}/>
<br />
Date: <input name="Date" type="date" value={this.state.field.Date} onChange={this.handleChange} />
<br />
<input type="submit" name="submit"  />
</form>
);
    }
}
export default Addbook;
