import React, { Component } from "react";

class Addbook extends Component{
    user;
    constructor(){
       super();
       this.state={
           field: {},        
           Bookid: "",
           BookName:"",
           Author:"",
           Edition:"",
           Date:"",
       }
       this.handleChange=this.handleChange.bind(this);
       this.SavaData=this.SavaData.bind(this);
    };
    componentDidMount(){
        this.user=JSON.parse(localStorage.getItem('field'))
        this.setState({
            Bookid:this.user.Bookid,
            BookName:this.user.BookName,
            Author:this.user.Author,
            Edition:this.user.Edition,
            Date:this.user.Date

        });
    }
    componentDidUpdate(nextState){
        localStorage.setItem('field',JSON.stringify(nextState.field));
    };
    handleChange = event => {
        let field=this.state.field;
        field[event.target.name]=event.target.value;
        this.setState({
            field
        });
    };
    SavaData =event =>{
    
        event.preventDefault();
        let field={};
        field["Bookid"]=" ";
        field["BookName"]="";
        field["Author"]="";
        field["Edition"]="";
        field["Date"]="";
        this.setState({field:field});
        
    };
    render(){
        return (
        <form id="form" onSubmit={this.SavaData}>
Bookid: <input name="id" type="number" value={this.state.field.Bookid} onChange={this.handleChange} />
<br />
BookName: <input name="Book-name" type="text" value={this.state.field.BookName} onChange={this.handleChange} />
<br />
Author: <input name="Name" type="text" value={this.state.field.Author} onChange={this.handleChange}/>
<br />
Edition: <input name="Name" type="text" value={this.state.field.Edition} onChange={this.handleChange}/>
<br />
Date: <input name="date" type="date" value={this.state.field.Date} onChange={this.handleChange} />
<br />
<input type="submit" name="submit"  />
</form>
);
    }
}
export default Addbook;
