    //         var field= JSON.parse(localStorage.getItem('field'));
    //         field.push(title);
    //         localStorage.setItem('field',JSON.stringify(field));
    //     }
    //     this.setState({
    //         field: JSON.parse(localStorage.getItem('field[]'))
    //     })
    // }
    render() {
        return (
        <form className="Display_form"  float='right' align='center' onSubmit={this.handleFormSubmit} >
            <Box float ="center" className="box">
                <div className="row">
                    <div className="form_1" >
                        <label for="Bookid">Bookid:</label>
                        </div>
                        <div className="form_1" >
                             <input name="Bookid" type="number" value={this.state.Bookid} ref='title' onChange={this.handleChange} />
                             <br />
                        </div>
                        </div>
                <div className="row">
                    <div className="form_1" >
                        <label for="BookName">BookName:</label>
                        </div>
                        <div className="form_1" >
                            <input name="BookName" type="text"  value={this.state.BookName} ref='title' onChange={this.handleChange} />
                            <br />
                            </div>
                            </div>
                <div className="row">
                    <div className="form_1" >
                        <label for="Author: ">Author:</label>
                        </div>
                        <div className="form_1" >
                            <input name="Author" type="text" className="form_1" value={this.state.Author} ref='title' onChange={this.handleChange}/>
                            <br />
                            </div>
                            </div>
                <div className="row">
                    <div className="form_1" >
                        <label for="Edition:">Edition:</label>
                        </div>
                        <div className="form_1" >
                            <input name="Edition" type="text" className="form_1" value={this.state.Edition} ref='title' onChange={this.handleChange}/>
                            <br />
                            </div>
                            </div>
                <div className="row">
                    <div className="form_1" >
                        <label for="Date:">Date:</label>
                        </div>
                        <div className="form_1" >
                            <input name="Date" type="date" className="form_1" value={this.state.Date} ref='title' onChange={this.handleChange} />
                            <br />
                            </div>
                            </div>
                <div className="row">
                    <div className="form_1" >
                        <button type="submit" >submit </button> 
                        </div>
                </div>{/* name="submit"  /> */}
        </Box>
    </form>
// {/* <div>
// {this.state.field && this.state.field.map((field) =>
//     (<field key={field} num={field} />))}
// </div> */}

);
        }
}   

